mapper.insert:
INSERT INTO coupons (
coupon_id, funding_id, user_id, store_id, coupon_code,
use_status, reviewed, created_at, expired_at
) VALUES (
coupon_seq.NEXTVAL, #{funding_id}, #{user_id}, #{store_id}, #{coupon_code},
#{use_status}, #{reviewed}, #{created_at}, #{expired_at}
)

mapper.update:
UPDATE coupons
SET use_status = #{use_status}
WHERE coupon_id = #{coupon_id}

UPDATE coupons
SET reviewed = 1
WHERE coupon_id = #{coupon_id}
